import math

def equacao(t, k):
    return math.exp(-0.5 * t) * math.acosh(math.exp(0.5 * t)) - math.sqrt(k / 2)

def bisec(f, a, b, tolerancia, max_iteracoes, k):
    if f(a, k) * f(b, k) > 0:
        print("O intervalo não engloba a raiz.")
        return None

    for iteracao in range(max_iteracoes):
        c = (a + b) / 2
        fc = f(c, k)

        if abs(fc) < tolerancia:
            print(f"Convergiu para a raiz depois de {iteracao} iterações.")
            return c

        if f(a, k) * fc < 0:
            b = c
        else:
            a = c

    print("Atingiu o número máximo de iterações. Convergência não alcançada.")
    return None

k = 0.67
vi_1 = 1.0
vi_2 = 200.0
tolerancia = 1e-6
max_iteracoes = 100

raiz = bisec(equacao, vi_1, vi_2, tolerancia, max_iteracoes, k)

if raiz is not None:
    print(f"A raiz da função é aproximadamente {raiz:.9f}.")
else:
    print("O método da bissecção não convergiu para uma raiz dentro das iterações permitidas.")